function core_oo($function) {\n    static $wrap = array(\n        'comment' => 'the_comment',\n        'comments' => 'have_comments',\n        'post' => 'the_post',\n        'posts' => 'have_posts'\n    );\n\n    // Validate that the function exists in the wrap array\n    if (!array_key_exists($function, $wrap)) {\n        throw new InvalidArgumentException("Function '{$function}' is not recognized.");\n    }\n\n    // Get arguments excluding the first one which is the function name\n    $args = array_slice(func_get_args(), 1);\n\n    // Call the wrapped function with the given arguments\n    return call_user_func_array($wrap[$function], $args);\n}