<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    if (!empty($_POST['data'])) {\n        $receivedData = $_POST['data'];\n        \n        // Decrypt data using the same key\n        $decryptedData = openssl_decrypt($receivedData, "AES-256-CBC", "your_encryption_key");\n        \n        echo "Received and decrypted XML: " . $decryptedData;\n    } else {\n        echo "No data received.";\n    }\n} else {\n    echo "Invalid request method.";\n}\n?>