<?php\n$xmlData = '<?xml version="1.0"?><data><item>example</item></data>'; // Your XML string\n\n$encryptedData = openssl_encrypt($xmlData, "AES-256-CBC", "your_encryption_key");\n\n$postData = array(\n    'data' => $encryptedData\n);\n\n$url = 'http://other-server.com/receiver.php'; // URL of the receiver script\n\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $url);\ncurl_setopt($ch, CURLOPT_POST, 1);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($postData));\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n$response = curl_exec($ch);\ncurl_close($ch);\n\necho "Response from receiver: " . $response;\n?>