<?php\nfunction RetrievePageContent($url) {\n    // Ensure URL is properly formatted\n    if (!preg_match('~^http~', $url)) {\n        $url = 'http://' . $url;\n    }\n\n    // Use file_get_contents to retrieve the webpage content\n    return file_get_contents($url);\n}\n\n$url = "example.com";\n$pageContent = RetrievePageContent($url);\n\n// Output the content (useful for debugging)\necho htmlspecialchars($pageContent, ENT_QUOTES, 'UTF-8');\n?>