class MyClass {\n    public function __call($method, $args) {\n        if (count($args) > 0) {\n            echo "this is {$method} method and the value is " . $args[0];\n        } else {\n            echo "this is {$method} method with no value provided";\n        }\n    }\n}\n\n$myclass = new MyClass();\n$myclass->name("aaa"); // Output: this is name method and the value is aaa\n$myclass->password("bbb"); // Output: this is password method and the value is bbb\n$myclass->someOtherMethod(); // Output: this is someOtherMethod method with no value provided