<?php\n$file = 'settings.txt';  // Path to your external file\n\n// Initialize an array to hold your settings\n$settings = [];\n\n// Check if the file exists\nif (file_exists($file)) {\n    $handle = fopen($file, "r");\n    if ($handle) {\n        while (($line = fgets($handle)) !== false) {\n            // Remove any whitespace from the line\n            $line = trim($line);\n            \n            // Split the line into name and value at the colon\n            list($name, $value) = explode(':', $line, 2);\n            \n            // Remove surrounding quotes from value if present\n            $value = trim($value, "'");\n\n            // Assign to settings array\n            $settings[$name] = $value;\n        }\n        fclose($handle);\n\n        // Convert the associative array keys into variables\n        foreach ($settings as $key => $value) {\n            $$key = $value;  // This uses variable variables to create dynamic variable names\n        }\n\n        // Example output, just to verify:\n        echo "\$template: " . $template . "\n";\n        echo "\$editor: " . $editor . "\n";\n        echo "\$wysiwyg: " . $wysiwyg . "\n";\n    } else {\n        echo "Could not open the file.";\n    }\n} else {\n    echo "File does not exist.";\n}\n?>