<?php\nsession_start();\n\n// Define your states\ndefine('VIEW', 'view');\ndefine('EDIT', 'edit');\ndefine('ADD', 'add');\n\n// Set initial state or retrieve it from session\nif (!isset($_SESSION['state'])) {\n    $_SESSION['state'] = VIEW;\n}\n\n// Handle form submission and change state accordingly\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    if (isset($_POST['action']) && $_POST['action'] === 'edit') {\n        $_SESSION['state'] = EDIT;\n        // Additional logic for edit action\n    } elseif (isset($_POST['action']) && $_POST['action'] === 'add') {\n        $_SESSION['state'] = ADD;\n        // Additional logic for add action\n    }\n}\n\n// Display content based on current state\nswitch ($_SESSION['state']) {\n    case VIEW:\n        echo '<h1>View Mode</h1>';\n        // Display form in view mode\n        break;\n    case EDIT:\n        echo '<h1>Edit Mode</h1>';\n        // Display form in edit mode\n        break;\n    case ADD:\n        echo '<h1>Add Mode</h1>';\n        // Display form for adding new data\n        break;\n}\n?>