<?php\nrequire_once 'Mail/Queue.php';\n\n$options = array(\n    'type' => 'db',\n    // other db configuration options\n);\n\n$mail_queue = &new Mail_Queue($options);\n$emails_sent = 0;\n$batch_size = 100; // emails per minute\n$wait_time = 60;   // seconds to wait between batches\n\nwhile (true) {\n    // Send a batch of emails\n    $sent = $mail_queue->sendMailsInQueue($batch_size);\n    if ($sent < $batch_size) {\n        // If less than the batch size was sent, we're done for now\n        break;\n    }\n    \n    // Wait one minute before continuing to the next batch\n    sleep($wait_time);\n}\n\n// Optionally log the total number of emails sent or other relevant data\necho "Sent a total of {$emails_sent} emails.";\n?>