$acl = new Zend_Acl();\n\n// Adding roles\n$acl->addRole(new Zend_Acl_Role('guest'));\n$acl->addRole(new Zend_Acl_Role('user'), 'guest');\n$acl->addRole(new Zend_Acl_Role('editor'), 'user');\n\n// Adding resources\n$acl->addResource(new Zend_Acl_Resource('all')); // Root resource\n$acl->addResource(new Zend_Acl_Resource('post'), 'all'); // Category: posts\n$acl->addResource(new Zend_Acl_Resource('gallery'), 'all'); // Category: galleries\n$acl->addResource(new Zend_Acl_Resource('photo_gallery'), 'gallery');\n$acl->addResource(new Zend_Acl_Resource('announcements_gallery'), 'gallery');\n\n// Adding specific instances as resources under their respective types\n$acl->addResource(new Zend_Acl_Resource('post_1'), 'post');\n$acl->addResource(new Zend_Acl_Resource('post_2'), 'post');\n$acl->addResource(new Zend_Acl_Resource('photo_gallery_1'), 'photo_gallery');\n\n// Defining permissions\n$acl->allow('guest', 'all', 'read'); // Guests can read everything\n$acl->deny('guest', 'post', 'edit');  // No editing for guests on posts\n\n$acl->allow('user', 'post', 'edit');  // Users can edit all posts\n\n$acl->deny('editor', 'all');           // Editors have no permissions globally unless explicitly allowed\n$acl->allow('editor', 'post_2', 'edit');// Editor can only edit post #2\n\n$acl->allow(null, 'photo_gallery_1', 'read') // Allow read access to this specific gallery for everyone\n// More specific rules as needed