<?php\n// Assuming $result is the result of the SQL query executed and fetched into an associative array format.\n$persons = [];\nforeach ($result as $row) {\n    if (!isset($persons[$row['person_id']])) {\n        // Initialize person entry if it doesn't exist yet\n        $persons[$row['person_id']] = [\n            'id' => $row['person_id'],\n            'name' => $row['name'],\n            'Photo' => []\n        ];\n    }\n    \n    if ($row['photo_id'] != null) {\n        // Add photo to the person's Photo array\n        $persons[$row['person_id']]['Photo'][] = [\n            'id' => $row['photo_id'],\n            'person_id' => $row['person_id'],\n            'path' => $row['path'],\n            'title' => $row['title']\n        ];\n    }\n}\n\n// Re-index the array if necessary\n$persons = array_values($persons);\nprint_r($persons);\n?>