// Assuming $html and $imageSrc are properly sanitized strings\n$html = "<P align=center><SPAN style=\"FONT-FAMILY: Arial\">Sample Text</SPAN></P>";\n$imageSrc = "https://example.com/removed.jpg"; // Example image URL\n\n// Encode the HTML as a JSON string to ensure it's correctly quoted in JavaScript\n$encodedHtml = json_encode($html);\n$encodedImageHtml = json_encode("<img src='" . $imageSrc . "' >");\n\necho '<a href="#" onclick="makewindows(' . $encodedHtml . '); return false;">Click for full description</a><br>';\necho '<a href="#" onclick="makewindows(' . $encodedImageHtml . '); return false;"><img src="' . htmlspecialchars($imageSrc) . '" width="250" height="250"></a>';