// Load your XML content\n$xml = simplexml_load_string($yourXmlString);\n\n// Use XPath to find the element\n$descriptionItems = $xml->xpath("//item[@title='0x|Beschrijving']");\n\nif (!empty($descriptionItems)) {\n    // Access the first item in the array and cast it to a string to get the CDATA content\n    $descriptionContent = (string) $descriptionItems[0];\n    echo "Description: " . htmlspecialchars($descriptionContent);\n} else {\n    echo "No description found.";\n}