<?php\n\n   namespace YourCompany\HelloWorld\Controller\Index;\n\n   use Magento\Framework\App\Action\Context;\n   use Magento\Framework\View\Result\PageFactory;\n   use YourCompany\HelloWorld\Model\ArticleFactory;\n\n   class ShowRow extends \Magento\Framework\App\Action\Action\n   {\n       protected $_pageFactory;\n       protected $_articleFactory;\n\n       public function __construct(\n           Context $context,\n           PageFactory $pageFactory,\n           ArticleFactory $articleFactory)\n       {\n           $this->_pageFactory = $pageFactory;\n           $this->_articleFactory = $articleFactory;\n           parent::__construct($context);\n       }\n\n       public function execute()\n       {\n           $id = 10;\n           /** @var \YourCompany\HelloWorld\Model\Article $article */\n           $article = $this->_articleFactory->create();\n           $article->load($id);\n\n           echo "ID: " . $article->getId() . "\n";\n           echo "Title: " . $article->getTitle() . "\n";\n           echo "Content: " . $article->getContent() . "\n";\n\n           return $this->_pageFactory->create();\n       }\n   }