<?php\n\nif(!defined('_VALID_ACCESS')) die('direct access is not allowed.');\ninclude('includes/connect.php'); \n\nfunction login($username, $password, $mysqli)\n{\n    $username = trim($username);\n    $password = trim($password);\n\n    // Note: This code is vulnerable to SQL injection. Use prepared statements instead.\n    $login_sql  = "SELECT * FROM user WHERE user = '".($username)."' \n                   AND pass = '".(md5($password))."'";\n    \n    $login_result = $mysqli->query($login_sql) or die(mysqli_error());\n    $row=$login_result->fetch_row();\n    \n    if ($row && $row[0] == 1)\n    {\n        return true;\n    }\n    else\n    {\n        return false;\n    }\n}\n\n// Usage of the login function - make sure to pass `$mysqli`\nif (login("your_username", "your_password", $mysqli)) {\n    echo "Login successful!";\n} else {\n    echo "Login failed.";\n}\n\n?>