<?php\n\nif(!defined('_VALID_ACCESS')) die('direct access is not allowed.');\ninclude('includes/connect.php');\n\nfunction login($username, $password, $mysqli)\n{\n    $username = trim($username);\n    $password = md5(trim($password)); // Note: Avoid using MD5 for password hashing. Use password_hash() and password_verify()\n\n    $stmt = $mysqli->prepare("SELECT * FROM user WHERE user = ? AND pass = ?");\n    $stmt->bind_param("ss", $username, $password);\n    \n    if ($stmt->execute()) {\n        $result = $stmt->get_result();\n        if ($row = $result->fetch_row()) {\n            return true;\n        }\n    }\n\n    return false;\n}\n\n// Usage of the login function - make sure to pass `$mysqli`\nif (login("your_username", "your_password", $mysqli)) {\n    echo "Login successful!";\n} else {\n    echo "Login failed.";\n}\n\n?>