<?php\nrequire_once dirname(__FILE__).'/shop/app/Mage.php';\nMage::app('default');\n\n// Set up the correct store and website IDs if necessary\n$storeId = Mage::app()->getStore()->getId();\n$websiteId = Mage::app()->getWebsite()->getId();\n\n// Initialize frontend session\n$session = Mage::getSingleton('core/session', array('name' => 'frontend'));\n\n// Use customer.session to check login status\n$customerSession = Mage::getSingleton('customer/session');\n\nif ($customerSession->isLoggedIn()) {\n    $customer = $customerSession->getCustomer();\n    echo "Hello, " . htmlspecialchars($customer->getName(), ENT_QUOTES) . "! <a href='/shop/customer/cart/'>View Cart</a>";\n} else {\n    echo "Please log in.";\n}\n?>