<?php\n// Ensure no buffering of any kind\nob_clean();\n\n// Get the image filename from GET request\n$filename = basename($_GET['image']);\n\n// Path where images are stored\n$imagePath = 'path/to/images/' . $filename;\n\nif (file_exists($imagePath)) {\n    // Set the appropriate content type header based on file extension\n    $finfo = finfo_open(FILEINFO_MIME_TYPE);\n    $mimeType = finfo_file($finfo, $imagePath);\n    finfo_close($finfo);\n\n    header('Content-Type: ' . $mimeType);\n    readfile($imagePath);\n} else {\n    // Handle the case where file does not exist\n    http_response_code(404);\n    echo "File not found.";\n}\n?>