<?php\n// Database connection\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Read the image file as binary data\n$imagePath = '../img/test.jpg';\n$imageBinary = file_get_contents($imagePath);\n\nif (!$imageBinary) {\n    die("Error reading the file.");\n}\n\n// Prepare a statement to prevent SQL injection and insert the image data\n$stmt = $mysqli->prepare("INSERT INTO table_name (picture) VALUES (?)");\n$stmt->bind_param("s", $imageBinary);\n\nif ($stmt->execute()) {\n    echo "Image uploaded successfully.";\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n// Close resources\n$stmt->close();\n$mysqli->close();\n?>