<?php\nsession_start();\n\n// Upon successful login\nfunction loginUser($userId) {\n    // Start a new session and store the user ID\n    $_SESSION['user_id'] = $userId;\n    // Optionally, create a custom User class instance and store it in the session\n    $_SESSION['user'] = new User($userId);\n}\n\n// On every page, check if the user is logged in\nfunction isLoggedIn() {\n    return isset($_SESSION['user_id']);\n}\n\nclass User {\n    public $id;\n    // Other properties like name, email, etc.\n\n    public function __construct($id) {\n        $this->id = $id;\n        // Fetch other data from database if needed\n    }\n}\n\n// Example usage\nif (isLoggedIn()) {\n    $user = $_SESSION['user'];\n    echo "Welcome, User ID: " . $user->id;\n} else {\n    echo "You are not logged in.";\n}\n?>