<?php\n// Example condition\n$condition = true; // This should be set based on your actual logic\n\nif ($condition) {\n    echo '<div id="login">\n        <h2>Login</h2>\n        <div class="box">\n            <form method="POST" action="index.php/login">\n                Username/Email:<br />\n                <input type="text" name="username" value="' . htmlspecialchars(set_value("username")) . '" size="50" class="form" />\n                ' . form_error("username") . '\n                Password:<br />\n                <input type="password" name="password" value="' . htmlspecialchars(set_value("password")) . '" size="50" class="form" />\n                ' . form_error("password") . '\n                <input type="submit" value="Login" name="login" />\n            </form>\n        </div>\n    </div>';\n} else {\n    echo '<div id="register">\n        <h2>Register</h2>\n        <div class="box">\n            <form method="POST" action="index.php/register">\n                Username:<br />\n                <input type="text" name="new_username" value="' . htmlspecialchars(set_value("new_username")) . '" size="50" class="form" />\n                ' . form_error("new_username") . '\n                Password:<br />\n                <input type="password" name="new_password" size="50" class="form" />\n                Confirm Password:<br />\n                <input type="password" name="confirm_password" size="50" class="form" />\n                ' . form_error("new_password") . '\n                <input type="submit" value="Register" name="register" />\n            </form>\n        </div>\n    </div>';\n}\n?>