<?php\n$host = 'localhost'; // Your host\n$dbuser = 'root';     // Database username\n$dbpass = '';         // Database password\n$dbname = 'your_db';  // Database name\n\n// Define the backup file path\n$backupFile = "backups/db-backup-" . date("Y-m-d-H-i-s") . ".sql";\n\n// Command to perform database backup\n$command = "mysqldump --opt -u $dbuser -$dbpass -h $host $dbname > $backupFile 2>&1";\n\n// Execute the command (this will produce a .sql file in your backups directory)\nsystem($command, $output);\n\nif ($output === 0) {\n    echo "Backup successful! The backup is stored in: $backupFile";\n} else {\n    echo "Error occurred during backup.";\n}\n?>