class UniqueObject {\n    private static $counter = 0;\n    private $objectId;\n\n    public function __construct() {\n        self::$counter++;\n        $this->objectId = self::$counter;\n    }\n\n    public function getObjectId() {\n        return $this->objectId;\n    }\n}\n\n// Usage example:\n$obj1 = new UniqueObject();\n$obj2 = new UniqueObject();\n\necho "Object 1 ID: " . $obj1->getObjectId() . "\n"; // Outputs Object 1 ID: 1\necho "Object 2 ID: " . $obj2->getObjectId() . "\n"; // Outputs Object 2 ID: 2