<?php\nfunction addStatusMsg($message) {\n    if (!isset($_SESSION['status_messages'])) {\n        $_SESSION['status_messages'] = [];\n    }\n    // Ensure data sanitization before storing\n    $sanitized_message = htmlspecialchars($message, ENT_QUOTES, 'UTF-8');\n    array_push($_SESSION['status_messages'], $sanitized_message);\n}\n\nfunction displayStatusMsg() {\n    if (isset($_SESSION['status_messages']) && !empty($_SESSION['status_messages'])) {\n        $messages = $_SESSION['status_messages'];\n        // Clear messages after displaying\n        unset($_SESSION['status_messages']);\n        \n        foreach ($messages as $message) {\n            echo "<div class='alert alert-info'>$message</div>";\n        }\n    }\n}\n?>