<?php\nsession_start();\n\n// Example of storing user categories in session after login\nif (isset($_POST['login'])) {\n    // Perform login validation and authentication\n    // If successful, regenerate session ID\n    session_regenerate_id(true);\n    \n    // Assume $userId is obtained from a secure login process\n    $userId = 1; // This should be dynamic based on user authentication\n\n    // Fetch categories for the user securely from the database\n    $userCategories = fetchUserCategoriesFromDatabase($userId);\n\n    // Store the categories in session\n    $_SESSION['user_categories'] = $userCategories;\n}\n\n// Example function to simulate fetching data from a database\nfunction fetchUserCategoriesFromDatabase($userId) {\n    // Simulated categories fetched from a database based on user ID\n    return ['Category1', 'Category2'];\n}\n?>