<?php\n\nfunction htmlToPdf($htmlContent) {\n    // Create a temporary HTML file\n    $tmpHtml = tempnam(sys_get_temp_dir(), 'html');\n    file_put_contents($tmpHtml, $htmlContent);\n\n    // Path to the wkhtmltopdf binary; adjust this path based on your installation\n    $wkhtmltopdfPath = '/usr/local/bin/wkhtmltopdf';\n\n    // Create a temporary PDF file\n    $tmpPdf = tempnam(sys_get_temp_dir(), 'pdf');\n\n    // Execute command to convert HTML to PDF\n    exec("{$wkhtmltopdfPath} {$tmpHtml} {$tmpPdf}", $output, $returnCode);\n\n    // Check for errors\n    if ($returnCode !== 0) {\n        throw new Exception('Error converting HTML to PDF: ' . implode("\n", $output));\n    }\n\n    // Read the PDF content\n    $pdfContent = file_get_contents($tmpPdf);\n\n    // Clean up temporary files\n    unlink($tmpHtml);\n    unlink($tmpPdf);\n\n    return $pdfContent;\n}\n\ntry {\n    // Example HTML content\n    $htmlContent = '<!DOCTYPE html><html><head><title>Test PDF</title></head><body><h1>Hello, PDF!</h1><p>This is a test.</p></body></html>';\n    \n    // Convert to PDF and output for download\n    $pdf = htmlToPdf($htmlContent);\n    header('Content-Type: application/pdf');\n    header('Content-Disposition: attachment; filename="test.pdf"');\n    echo $pdf;\n} catch (Exception $e) {\n    die("Error: " . $e->getMessage());\n}\n\n?>