<?php\n\nif (!empty($_POST['data']) && !empty($_POST['iv'])) {\n    $encodedData = base64_decode($_POST['data'], true);\n    $iv = base64_decode($_POST['iv'], true);\n\n    // Key derivation should match the Cocoa side's EVP_BytesToKey\n    $key = hash('md5', "ThisIsMyKey", true); // This matches your key length (16 bytes)\n\n    // OpenSSL needs to specify the padding explicitly\n    $decodedData = openssl_decrypt($encodedData, 'BF-CBC', $key, OPENSSL_RAW_DATA | OPENSSL_ZERO_PADDING, $iv);\n\n    // Remove any PKCS7-style padding if necessary\n    $paddingLength = ord($decodedData[strlen($decodedData) - 1]);\n    $decodedData = substr($decodedData, 0, -$paddingLength);\n\n    echo "decoded: " . trim($decodedData);\n}\n?>