function dbConnect() {      \n    $conn = new mysqli(DB_SERVER, DB_USER, DB_PASSWORD, DB_NAME) or die ('Error.');\n    \n    // Set the character set to utf8mb4\n    if (!$conn->set_charset("utf8mb4")) {\n        die('Error loading character set utf8mb4: ' . $conn->error);\n    }\n\n    return $conn;\n}\n\n$conn = dbConnect();\n$stmt = $conn->stmt_init();\n\n// Example data fetching and cleaning\n$rawData = '&ntilde;&amp;quot;&#x27;';\n$decodedData = html_entity_decode($rawData, ENT_QUOTES, 'UTF-8');\n\n// Prepare your SQL statement\nif ($stmt->prepare('INSERT INTO your_table (your_column) VALUES (?)')) {\n    $stmt->bind_param("s", $decodedData);\n    $stmt->execute();\n    echo "Data inserted successfully.";\n} else {\n    die('Prepare failed: ' . $conn->error);\n}\n\n$stmt->close();\n$conn->close();