public function row2Partner($row) {\n    if (is_object($row)) {\n        echo $row->PartnerID;\n    } else {\n        echo "No data object received.";\n    }\n}\n\npublic function main() {\n    $connection = new mysqli("your_host", "your_username", "your_password", "your_db");\n\n    if ($connection->connect_error) {\n        die("Connection failed: " . $connection->connect_error);\n    }\n\n    $query = "SELECT PartnerID, PartnerName FROM Partner";\n    $result = $connection->query($query);\n\n    if (!$result) {\n        die("Query failed: " . $connection->error);\n    }\n\n    while ($row = $result->fetch_object()) {\n        $this->row2Partner($row);\n    }\n\n    if ($result->num_rows == 0) {\n        echo "No rows returned from the query.";\n    }\n\n    $result->free();\n    $connection->close();\n}