public function row2Partner($row) {\n    if (is_object($row)) {\n        echo $row->PartnerID;\n    } else {\n        echo "No data object received.";\n    }\n}\n\npublic function main() {\n    $query = "SELECT PartnerID, PartnerName FROM Partner";\n    $result = mysql_query($query);\n\n    if ($result === false) {\n        die(mysql_error()); // Check for query execution error\n    }\n\n    while ($row = mysql_fetch_object($result)) { // Use a loop to handle all rows\n        $this->row2Partner($row);\n    }\n\n    if (mysql_num_rows($result) == 0) {\n        echo "No rows returned from the query.";\n    }\n}