class QueryBuilder {\n    private $query = [];\n\n    public function select($fields) {\n        $this->query['select'] = $fields;\n        return $this; // This allows chaining\n    }\n\n    public function where($condition) {\n        $this->query['where'][] = $condition;\n        return $this;\n    }\n\n    public function build() {\n        // Build the SQL query string from $this->query\n        return "SELECT {$this->query['select']} WHERE " . implode(" AND ", $this->query['where']);\n    }\n}\n\n// Usage:\n$query = (new QueryBuilder)->select('*')->where('age > 18')->build();\necho $query; // Outputs: SELECT * WHERE age > 18