<?php\n// Assume database connection already established with $conn\n$id = intval($_GET['id']);\n\n// Use prepared statements to prevent SQL injection\n$stmt = $conn->prepare("SELECT column1, column2, column3 FROM table WHERE id = ?");\n$stmt->bind_param("i", $id);\n$stmt->execute();\n$result = $stmt->get_result();\n\n$data = [];\nwhile ($row = $result->fetch_assoc()) {\n    $data[] = [\n        'column1' => htmlspecialchars($row['column1']),\n        'column2' => htmlspecialchars($row['column2']),\n        'column3' => htmlspecialchars($row['column3'])\n    ];\n}\n\n$stmt->close();\n$conn->close();\n\n// Return data to be used in the view\nreturn $data;\n?>