<?php\n   // This is a simple example and not production ready. \n   // In practice, always use password_hash() for storing passwords and compare them using password_verify().\n   \n   $valid_username = "member";\n   $valid_password = "securepassword";\n\n   if ($_SERVER["REQUEST_METHOD"] == "POST") {\n       $username = $_POST['username'];\n       $password = $_POST['password'];\n\n       // Basic validation\n       if ($username === $valid_username && $password === $valid_password) {\n           header("Location: welcome.html");\n           exit(); // Important to stop the script execution after redirecting\n       } else {\n           echo "Invalid username or password. Please try again.";\n       }\n   }\n   ?>