<?php\n$url = "https://example.com/soapserver.php";\n\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false); // For testing purposes only! Should be true in production.\ncurl_setopt($ch, CURLOPT_NOBODY, true); // We don't need the body\n\n$response = curl_exec($ch);\n\nif ($response === FALSE) {\n    $info = curl_getinfo($ch);\n    curl_close($ch);\n    die('Error occurred: ' . var_export($info, true));\n}\n$httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\ncurl_close($ch);\n\nif ($httpCode == 200) {\n    echo "URL is reachable via HTTPS.";\n} else if ($httpCode == 403) {\n    echo "Forbidden URL.";\n} else {\n   echo "HTTP error: $httpCode";\n}\n?>