try {\n    $pdo = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n    $stmt = $pdo->query("\n        SELECT CONCAT(fname, ', ', sname) AS name, email.address AS email\n        FROM contacts\n        LEFT JOIN emails ON contacts.contactID = emails.contactID\n    ");\n\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        $emailLink = isset($row['email']) && !is_null($row['email'])\n            ? "<a href=\"mailto:" . htmlspecialchars($row['email']) . "\">" . htmlspecialchars($row['name']) . "</a>"\n            : htmlspecialchars($row['name']);\n        echo "<li>$emailLink</li>";\n    }\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}