<?php\n// Your document content\n$documentContent = "This is the content to print.";\n\n// Path to a temporary file\n$tempFilePath = tempnam(sys_get_temp_dir(), 'print_');\n\n// Write your content to the temporary file\nfile_put_contents($tempFilePath, $documentContent);\n\n// Execute the lp command to send the file to the printer\n$printerName = "YourPrinterName"; // Replace with your actual printer name\nexec("lp -d $printerName $tempFilePath", $output, $returnVar);\n\nif ($returnVar === 0) {\n    echo "Print job sent successfully.";\n} else {\n    echo "An error occurred while sending the print job. Output: " . implode("\n", $output);\n}\n\n// Optionally delete the temporary file\nunlink($tempFilePath);\n?>