<?php\n\n$array = [\n    ["abc", "def"],\n    ["ghi", "jkl"],\n    ["mno", "pql"],\n    ["abc", "def"],\n    ["ghi", "jkl"],\n    ["mno", "pql"]\n];\n\n// Use array_map with serialize to create unique keys\n$uniqueArray = array_unique(array_map('serialize', $array));\n\n// Unserialize and reindex the array\n$resultArray = array_values(array_map('unserialize', $uniqueArray));\n\nprint_r($resultArray);\n?>