class Config {\n    private static $config = [];\n\n    public static function init($file) {\n        if (!file_exists($file)) {\n            throw new Exception('Configuration file not found');\n        }\n        self::$config = json_decode(file_get_contents($file), true);\n        if (self::$config === null && json_last_error() !== JSON_ERROR_NONE) {\n            throw new Exception('Error decoding configuration JSON');\n        }\n    }\n\n    public static function get($key, $default = null) {\n        return array_key_exists($key, self::$config) ? self::$config[$key] : $default;\n    }\n}\n\n// Initialize and use\nConfig::init(__DIR__ . '/config.json');\n$dbHost = Config::get('db_host');