<?php\n// Database connection\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database_name";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// User input for rows and columns (ensure these are validated)\n$numberOfRows = 3;\n$numberOfColumns = 3;\n\n// Fetch data from database\n$sql = "SELECT your_column_name FROM your_table_name LIMIT 1";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    // Output data of each row\n    while($row = $result->fetch_assoc()) {\n        $dataString = $row["your_column_name"];\n        $dataArray = explode(",", $dataString);\n\n        if (count($dataArray) >= $numberOfRows * $numberOfColumns) {\n            echo "<table border='1'>\n";\n            for ($i = 0; $i < $numberOfRows; $i++) {\n                echo "<tr>\n";\n                for ($j = 0; $j < $numberOfColumns; $j++) {\n                    echo "<td>___" . htmlspecialchars($dataArray[$i * $numberOfColumns + $j]) . "__</td>\n";\n                }\n                echo "</tr>\n";\n            }\n            echo "</table>";\n        } else {\n            echo "Not enough data to fill the table.";\n        }\n    }\n} else {\n    echo "0 results";\n}\n\n$conn->close();\n?>