$dateToday = new DateTime();\n   $specificDate = new DateTime('2008-06-26');\n   \n   // Subtract 2008-06-26 from today\n   $interval = $dateToday->diff($specificDate);\n   echo "Difference: " . $interval->y . " years, " . $interval->m . " months, and " . $interval->d . " days.\n";\n   \n   // Subtract one week from today\n   $oneWeekAgo = clone $dateToday;\n   $oneWeekAgo->modify('-1 week');\n   echo "One week ago was: " . $oneWeekAgo->format('Y-m-d') . "\n";