<?php\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\n// Set user ID\n$userId = 1; // This should be sanitized and validated if coming from user input\n\n// Prepare the statement\n$stmt = $mysqli->prepare("CALL GetUserByUserId(?, @outId, @outName)");\n$stmt->bind_param('i', $userId);\n\n// Execute the stored procedure\nif ($stmt->execute()) {\n    // Fetch results back to PHP variables\n    $stmt->close();\n\n    $resultStmt = $mysqli->query("SELECT @outId AS id, @outName AS name");\n    if ($resultRow = $resultStmt->fetch_assoc()) {\n        echo "User ID: " . $resultRow['id'] . ", Name: " . $resultRow['name'];\n    } else {\n        echo "No user found with the given ID.";\n    }\n} else {\n    echo "Stored procedure execution failed.";\n}\n?>