class Config {\n    // Define constants (global variables) here\n    const DB_HOST = 'localhost';\n    const DB_USER = 'user';\n    const DB_PASS = 'password';\n    const DB_NAME = 'example_db';\n\n    // Static method to retrieve a constant value by name\n    public static function get($key, $default = null) {\n        if (defined("static::{$key}")) {\n            return constant("static::{$key}");\n        }\n        return $default;\n    }\n}\n\n// Usage example\n$dbHost = Config::get('DB_HOST');