class DBCom {\n       var $queryString;\n       var $queryResult;\n       var $queryArray;\n\n       function __construct($qS = '') {\n           $this->queryString = $qS;\n           if ($this->queryString !== '') {\n               $this->queryResult = mysqli_query($connection, $this->queryString) or die("Query failed: " . mysqli_error($connection));\n           }\n           $this->queryArray = array();\n       }\n\n       function get_query_array() {\n           if (empty($this->queryString)) {\n               die("Query String is Empty. Cannot Proceed.");\n           }\n           while ($fetchedArray = mysqli_fetch_array($this->queryResult, MYSQLI_ASSOC)) {\n               $this->queryArray[] = $fetchedArray;\n           }\n           return $this->queryArray;\n       }\n   }