<?php\n$mmddyyyy_date = "12/31/2023"; // Example input date in MM/DD/YYYY format\n\ntry {\n    // Convert the date from MM/DD/YYYY to YYYY-MM-DD\n    $dateObject = DateTime::createFromFormat('m/d/Y', $mmddyyyy_date);\n    if (!$dateObject) {\n        throw new Exception("Invalid date format.");\n    }\n    $ymd_date = $dateObject->format('Y-m-d');\n\n    // Connect to MySQL database\n    $pdo = new PDO('mysql:host=localhost;dbname=testdb;charset=utf8mb4', 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare and execute the SQL statement\n    $stmt = $pdo->prepare("INSERT INTO your_table (date_column) VALUES (:date)");\n    $stmt->execute([':date' => $ymd_date]);\n\n    echo "Date inserted successfully.";\n} catch (Exception $e) {\n    echo "Error: " . $e->getMessage();\n}\n?>