function checkKeyViaAPI($serverUrl, $apiKey) {\n    $ch = curl_init();\n\n    curl_setopt($ch, CURLOPT_URL, $serverUrl);\n    curl_setopt($ch, CURLOPT_POST, 1);\n    curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query(['key' => $apiKey]));\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n    $response = curl_exec($ch);\n    curl_close($ch);\n\n    return json_decode($response, true);\n}\n\n$isValid = checkKeyViaAPI('https://yourjoomlasite.com/api/check-key.php', $registrationKey);\nif ($isValid) {\n    echo "The key is valid.";\n} else {\n    echo "Invalid or expired key.";\n}