// The email timestamp\n$emailTimestamp = 'Sun, 21 Jun 2009 20:21:13 +0200';\n\n// Create a DateTime object from the timestamp with the correct timezone\n$dateTime = new DateTime($emailTimestamp);\n\n// Convert to UTC\n$dateTime->setTimezone(new DateTimeZone('UTC'));\n\n// Output the UTC time\necho "UTC Time: " . $dateTime->format('Y-m-d H:i:s') . "\n";\n\n// Convert back to local timezone (e.g., UTC+2)\n$localTimezone = new DateTimeZone('Europe/Berlin'); // Berlin is typically UTC+2, adjust as needed\n$dateTime->setTimezone($localTimezone);\n\n// Output the local time\necho "Local Time: " . $dateTime->format('Y-m-d H:i:s') . "\n";