<?php\n\nfunction processBlogText($text) {\n    // Find all <pre><code>...</code></pre> blocks\n    preg_match_all('/(<pre><code>(.*?)<\/code><\/pre>)/s', $text, $matches, PREG_SET_ORDER);\n\n    // Store found code blocks in an array and replace them with placeholders\n    $placeholders = [];\n    foreach ($matches as $index => $match) {\n        $placeholders['%%CODE_BLOCK_' . $index . '%%*/'] = $match[0];\n        $text = str_replace($match[0], '%%CODE_BLOCK_' . $index . '%%*/', $text);\n    }\n\n    // Process the text (example: replace quotes with typographic ones)\n    // Here's a simple example of such processing\n    $processedText = preg_replace('/"([^"]*)"/', '“$1”', $text);\n\n    // Reinsert code blocks into their original positions using placeholders\n    foreach ($placeholders as $placeholder => $codeBlock) {\n        $processedText = str_replace($placeholder, $codeBlock, $processedText);\n    }\n\n    return $processedText;\n}\n\n// Example usage\n$htmlContent = "<p>This is a \"quote\" in the text.</p><pre><code>echo 'Hello World';</code></pre><p>Another \"quote\".</p>";\n$finalHtmlContent = processBlogText($htmlContent);\necho htmlspecialchars($finalHtmlContent);\n\n?>