session_start();\ninclude 'db.php';\n\n$token = isset($_SERVER['HTTP_X_AUTH_TOKEN']) ? $_SERVER['HTTP_X_AUTH_TOKEN'] : '';\n\nif ($token) {\n    $stmt = $pdo->prepare("SELECT user_id FROM sessions WHERE token = ?");\n    if ($stmt->execute([$token])) {\n        $session = $stmt->fetch();\n        if ($session) {\n            $_SESSION['user_id'] = $session['user_id'];\n            // Proceed with loading the page\n        } else {\n            http_response_code(403);\n            echo "Session expired or invalid.";\n        }\n    }\n} else {\n    http_response_code(401);\n    echo "Not authenticated.";\n}