<?php\n// Example PHP associative array (from session)\n$sessionData = ['name' => 'John Doe', 'age' => 30, 'email' => 'john.doe@example.com'];\n\n// Convert PHP array to XML\n$xmlString = '<root>';\nforeach ($sessionData as $key => $value) {\n    $xmlString .= "<item><key>$key</key><value>$value</value></item>";\n}\n$xmlString .= '</root>';\n\n$domXml = new DOMDocument();\n$domXml->loadXML($xmlString);\n\n// Load XSLT file\n$xsl = new DOMDocument();\n$xsl->load('stylesheet.xslt');\n\n// Configure the transformer\n$processor = new XSLTProcessor();\n$processor->importStylesheet($xsl);\n\n// Transform XML to HTML or other format as per your stylesheet\necho $processor->transformToXML($domXml);\n?>