class Forum_Controller extends Controller {\n\n    function __construct()\n    {\n        parent::__construct();\n    }\n\n    function index()\n    {\n        // Use $this-> here since categories is an instance method\n        echo $this->categories();\n    }\n\n    /*\n     * get a list of categories from a specific site.\n     */\n    private function categories()  // This should not be static if it uses instance properties like $this->site_id\n    {\n        $db = new Database;\n        $categories = $db->query("\n            SELECT * FROM\n            forum_categories\n            WHERE fk_site = '{$this->site_id}'  // Avoid direct string interpolation, use prepared statements or placeholders for security\n        ");\n        $view = new View('categories_view');\n        $view->categories = $categories;\n        return $view;\n    }\n\n}