session_start();\ninclude 'db.php'; // Connect to your DB\n\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $username = $_POST['username'];\n    $password = $_POST['password'];\n\n    // Query to find user\n    $stmt = $pdo->prepare("SELECT * FROM users WHERE username = ?");\n    $stmt->execute([$username]);\n    $user = $stmt->fetch();\n\n    if ($user && password_verify($password, $user['password'])) {\n        // Generate a unique token\n        $token = bin2hex(random_bytes(32));\n\n        // Store the session data in the database\n        $stmt = $pdo->prepare("INSERT INTO sessions (user_id, token) VALUES (?, ?)");\n        if ($stmt->execute([$user['id'], $token])) {\n            // Set a custom header or use another method to send the token\n            header('X-AUTH-TOKEN: ' . $token);\n            echo "Login successful!";\n        } else {\n            echo "Failed to create session.";\n        }\n    } else {\n        echo "Invalid credentials.";\n    }\n}